const Listing = require("../models/listing");

module.exports.index = async (req, res) => {
  const alllistings = await Listing.find({});
  res.render("listings/index.ejs", { listings: alllistings });
};

module.exports.renderNewForm = (req, res) => {
    res.render("listings/new.ejs");
};

module.exports.showListing = async (req, res) => {
  let { id } = req.params;
  let listing = await Listing.findById(id).populate({path : 'reviews', populate: {path: 'author'}}).populate('owner');
  if (!listing) {
    req.flash('error_msg', 'Listing not found');
    return res.redirect('/listings');
  }
  console.log(listing);
  res.render("listings/show.ejs", { listing , currentUser: req.user });
  await listing.save();
};

module.exports.createListing = async (req, res) => {
  let url = req.file.path;
  let filename = req.file.filename;
  
  let listing = new Listing(req.body.listing);
  console.log(req.user);
  listing.owner = req.user._id; // Set the owner to the currently logged-in user
  listing.image = { url, filename };
  await listing.save();
  req.flash('success_msg', 'Listing created successfully');
  res.redirect("/listings");
};

module.exports.editListing = async (req, res) => {
  let { id } = req.params;
  let listing = await Listing.findById(id);
  if (!listing) {
    return res.redirect('/listings');
  }
  
  res.render("listings/edit.ejs", { listing });
};

module.exports.updateListing = async (req, res) => {
  let { id } = req.params;
  let listing =  await Listing.findById(id);
  if(!listing.owner._id.equals(res.locals.currentUser._id)) {
    req.flash('error_msg', 'You do not have permission to edit this listing');
    return res.redirect(`/listings/${id}`);
  }

  let listingup = await Listing.findByIdAndUpdate(id, { ...req.body.listing });
  if (req.file) {
    let url = req.file.path;
    let filename = req.file.filename;
    listingup.image = { url, filename };
    await listingup.save();
  }
  req.flash('success_msg', 'Listing updated successfully');
  res.redirect(`/listings/${listing._id}`);
};

module.exports.deleteListing = async (req, res) => {
  let { id } = req.params;
  let deletedListing = await Listing.findByIdAndDelete(id);
  console.log(`Deleted listing: ${deletedListing.title}`);
  req.flash('success_msg', 'Listing deleted successfully');
  res.redirect("/listings");
};

const Listing = require("../models/listing");
const Review = require('../models/reviews');


module.exports.createReview = async (req, res) => {
  let listing = await Listing.findById(req.params.id);
  let newReview = new Review(req.body.review);
  newReview.author = req.user._id; // Set the author to the currently logged-in user
  console.log(newReview);
  listing.reviews.push(newReview);
  await newReview.save();
  await listing.save();
    req.flash('success_msg', 'Review added successfully');
  res.redirect(`/listings/${listing._id}`);
};

module.exports.deleteReview = async (req, res) => {
  const { id, reviewId } = req.params;
  await Listing.findByIdAndUpdate(id, { $pull: { reviews: reviewId } });
  await Review.findByIdAndDelete(reviewId);
  req.flash('success_msg', 'Review deleted successfully');
  res.redirect(`/listings/${id}`);
};

const User = require('../models/user');

module.exports.signupForm = (req, res) => {
  res.render('users/signup.ejs');
};    

module.exports.signup  = async (req, res, next) => {
  const { username, email, password } = req.body; // <-- now matches form fields
  try {
    const newUser = new User({ username, email });
    const registeredUser = await User.register(newUser, password);

    req.login(registeredUser, (err) => {
      if (err) {
        req.flash('error_msg', 'Error during login after signup');
        return res.redirect('/signup');
      }
      req.flash('success_msg', 'Welcome to Hotel App!');
      res.redirect('/listings'); // send to listings instead of login page
    });

  } catch (err) {
    if (err.name === 'UserExistsError') {
      req.flash('error_msg', 'Username already exists');
    } else {
      req.flash('error_msg', 'Registration failed: ' + err.message);
    }
    res.redirect('/signup');
  }
};

module.exports.loginForm = (req, res) => {
  res.render('users/login.ejs');
};

module.exports.login = (req, res) => {
    req.flash('success_msg', 'Welcome back!');
    res.redirect(res.locals.redirectUrl || '/listings');
};

module.exports.logout = (req, res) => {
  req.logout((err) => {
    if (err) {
      req.flash('error_msg', 'Error logging out');
    } else {
      req.flash('success_msg', 'You have logged out successfully');
    }
    res.redirect('/login');
  });
};

